mapper.insert:

  	INSERT INTO BM_BO_EFFECT (
	      bo_seq_no
		, effect_class
		, effect_start
		, effect_end
		, effect_del
	) VALUES (
	      #{bo_seq_no}
		, #{effect_class}
		, SYSDATE
		, SYSDATE + 3
	);

mapper.update:

  	UPDATE bm_bo_effect SET
		effect_del = 'Y'
	WHERE bo_seq_no = #{bo_seq_no}
	AND effect_class = #{effect_class}
